#include <igraph.h>
#include <stdlib.h>

int main() {

  long int i;
  for (i=0; i<1000; i++) {
    igraph_real_t r=igraph_rng_get_unif01(igraph_rng_default());
    if (r<0 || r>1) {
      return 1;
    }
  }

  return 0;
}
